package week1.day2;

import java.util.Arrays;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 10; i++) {
			if (i % 2 != 0) {
				System.out.println("Odd Number: " + i);
			} else {
				System.out.println(i + " is not a odd number");
			}
			
		}

	}

}
